function generateUID () {
  return (
    Math.random()
      .toString(36)
      .substring(2, 15) +
    Math.random()
      .toString(36)
      .substring(2, 15)
  )
}

export function _getUsers () {
  return new Promise((resolve, reject) => {
    setTimeout(
      () =>
        resolve({
          ...users
        }),
      1000
    )
  })
}

export function _getQuestions () {
  return new Promise((resolve, reject) => {
    setTimeout(
      () =>
        resolve({
          ...questions
        }),
      1000
    )
  })
}

function formatQuestion ({
  optionOneText,
  optionTwoText,
  author
}) {
  return {
    id: generateUID(),
    timestamp: Date.now(),
    author,
    optionOne: {
      votes: [],
      text: optionOneText
    },
    optionTwo: {
      votes: [],
      text: optionTwoText
    }
  }
}

export function _saveQuestion (question) {
  return new Promise((resolve, reject) => {
    const formattedQuestion = formatQuestion(question)

    setTimeout(() => {
      questions = {
        ...questions,
        [formattedQuestion.id]: formattedQuestion
      }

      users = {
        ...users,
        [formattedQuestion.author]: {
          ...users[formattedQuestion.author],
          questions: [...users[formattedQuestion.author].questions, formattedQuestion.id]
        }
      }

      resolve(formattedQuestion)
    }, 1000)
  })
}

export function _saveQuestionAnswer ({
  authedUser,
  qid,
  answer
}) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      users = {
        ...users,
        [authedUser]: {
          ...users[authedUser],
          answers: {
            ...users[authedUser].answers,
            [qid]: answer
          }
        }
      }

      questions = {
        ...questions,
        [qid]: {
          ...questions[qid],
          [answer]: {
            ...questions[qid][answer],
            votes: questions[qid][answer].votes.concat([authedUser])
          }
        }
      }

      resolve()
    }, 500)
  })
}

export function _saveNewUser ({ username, name }) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      Object.keys(users).map(user => {
        if (user === username) {
          resolve({
            error: 'Username Already Taken'
          })
        }
      })
      users = {
        ...users,
        [username]: {
          id: username,
          name,
          avatarURL: '',
          answers: {},
          questions: []
        }
      }

      resolve({
        ...users
      })
    }, 1000)
  })
}
